import React, { useState, useEffect } from 'react';

const Home = () => {
    const [inputName, setInputName] = useState('');

    const handleSubmit = () => {
        setInputName()
        const userInput = { name: inputName }; // New access code to add
        fetch('http://localhost:3001/names', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify(userInput),
        })
        .then((response) => {
            alert('Name sent successfully, ', inputName);
        })

        // Clear the input field after submission
        setInputName('');
    }

    return(
        <div className="homePageDiv bg-dark text-light p-4">
            <center>
                <input className="homePageInput" placeholder="enter your name" onChange={(e) => setInputName(parseInt(e.target.value))}></input><br></br>
                <button id="homePageSubmitButton" onClick={handleSubmit}>
                    Submit
                </button>
            </center>
        </div>
    );
}

export default Home;
