import React, { useState } from 'react';

const Home = () => {
    const [inputName, setInputName] = useState(''); // Initialize inputName with an empty string
    const [names, setNames] = useState([]); // Initialize names as an empty array to store the fetched names
    const [showGreeting, setShowGreeting] = useState(false); // Initialize a state variable to control the display of the greeting

    const handleSubmit = () => {
        const userInput = { name: inputName };
        fetch('http://localhost:3001/names', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userInput),
        })
        .then((response) => response.json()) // Assuming the server responds with a JSON array of names
        .then((data) => {
            setNames(data); // Update the names state with the fetched names
            setShowGreeting(true); // Show the greeting and names
        })
        .catch((error) => {
            // Handle errors here if needed
        });

        setInputName('');
    }

    return (
        <div className="homePageDiv bg-dark text-light p-4">
            <center>
                <input
                    className="homePageInput"
                    placeholder="enter your name"
                    value={inputName}
                    onChange={(e) => setInputName(e.target.value)}
                /><br />
                <button id="homePageSubmitButton" onClick={handleSubmit}>
                    Submit
                </button>
            </center>

            {showGreeting && (
                <div>
                    <p>Hello, World</p>
                    <p>List of Names:</p>
                    <ul>
                        {names.map((name, index) => (
                            <li key={index}>{name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default Home;
