const Home = () => {
    const [inputName, setInputName] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault()
        const userInput = { name: inputName }; // New access code to add
        fetch('http://localhost:3001/access-codes', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json',
            },
            body: JSON.stringify(userInput),
        })
    }

    return(
        <div className="homePageDiv bg-dark text-light p-4">
            <center>
                <input className="homePageInput" placeholder="enter your name"></input><br></br>
                <button id="homePageSubmitButton" onClick={handleSubmit} onChange={(e) => setInputName(parseInt(e.target.value))}>
                    Submit
                </button>
            </center>
        </div>
    );
}

export default Home;
