import React, { useState } from 'react';

const Home = () => {
    const [inputName, setInputName] = useState(''); 
    const [names, setNames] = useState([]); 
    const [showGreeting, setShowGreeting] = useState(false);

    const handleSubmit = () => {
        const userInput = { name: inputName };
        fetch('http://localhost:3001/names', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userInput),
        })

        fetch('http://localhost:3001/access-codes')
        .then(response => response.json())
        .then(data => {
            setAllAccessCodes(data); // Update the state with the fetched access codes
        })
        .then((data) => {
            setNames(data);
            setShowGreeting(true);
        })
        setInputName('');
    }

    return (
        <div className="homePageDiv bg-dark text-light p-4">
            <center>
                <input
                    className="homePageInput"
                    placeholder="enter your name"
                    value={inputName}
                    onChange={(e) => setInputName(e.target.value)}
                /><br />
                <button id="homePageSubmitButton" onClick={handleSubmit}>
                    Submit
                </button>
            </center>

            {showGreeting && (
                <div>
                    <p>Hello, World</p>
                    <p>List of Names:</p>
                    <ul>
                        {names.map((name, index) => (
                            <li key={index}>{name}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default Home;
