import React, { useState } from 'react';

const Home = () => {
    const [inputName, setInputName] = useState(''); // Initialize inputName with an empty string

    const handleSubmit = () => {
        const userInput = { name: inputName }; // Create userInput with the current value of inputName
        fetch('http://localhost:3001/names', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userInput),
        })
        .then((response) => {
            // Handle the response from the server here if needed
        })
        .catch((error) => {
            // Handle errors here if needed
        });

        // Clear the input field after submission
        setInputName('');
    }

    return (
        <div className="homePageDiv bg-dark text-light p-4">
            <center>
                <input
                    className="homePageInput"
                    placeholder="enter your name"
                    value={inputName} // Set the input field value to inputName
                    onChange={(e) => setInputName(e.target.value)} // Update inputName with the input field value
                /><br />
                <button id="homePageSubmitButton" onClick={handleSubmit}>
                    Submit
                </button>
            </center>
        </div>
    );
}

export default Home;
